import power from './power.jpg';
import king from './king.jpg';
import monster from './monster.jpg';
import './App.css';
import { useEffect, useRef, useState } from 'react';
import { Annotorious } from '@recogito/annotorious';
import '@recogito/annotorious/dist/annotorious.min.css';


function App() {

  // Ref to the image DOM element
  const imgE = useRef();

  // The current Annotorious instance
  const [ anno, setAnno ] = useState();

  // Current drawing tool name
  const [ tool, setTool ] = useState('rect');

  // Current Annotations
  const [ strannos, setStrannos ] = useState('no annotations yet');
  var annotations;

  // Init Annotorious when the component
  // mounts, and keep the current 'anno'
  // instance in the application state
  useEffect(() => {
    let annotorious = null;

    if (imgE.current) {
      // Init
      annotorious = new Annotorious({
        image: imgE.current,
      });

      //annotations = annotorious.getAnnotations();
      //stranno = annotations;

      // Attach event handlers here
      annotorious.on('createAnnotation', annotation => {
        console.log('created', annotation);
        //console.log('annotations', annotations);
      });

      annotorious.on('updateAnnotation', (annotation, previous) => {
        console.log('updated', annotation, previous);
      });

      annotorious.on('deleteAnnotation', annotation => {
        console.log('deleted', annotation);
      });


    }


    // Keep current Annotorious instance in state
    setAnno(annotorious);


    // Cleanup: destroy current instance
    return () => annotorious.destroy();
  }, []);


  // Toggles current tool + button label
  const toggleTool = () => {
    if (tool === 'rect') {
      setTool('polygon');
      anno.setDrawingTool('polygon');
    } else {
      setTool('rect');
      anno.setDrawingTool('rect');
    }
  }


  const showAnno = () => {
    //const annotations = annotorious.getAnnotations();
    // stranno = annotations.body[0].value
    annotations = anno.getAnnotations();
    console.log('Existing Annotations:', annotations);
    var temp = "";
    if(annotations.length > 0){
      for(let i = 0; i < annotations.length; i++){
        temp = temp + annotations[i].body[0].value
        if(i != annotations.length-1){
            temp = temp + ", "
        }
      }
        setStrannos(temp);
    }
    //stranno = annotations[0].body[0].value
  }

  var timestamp = new Date().getTime();
  var image =document.getElementById("img");
  //var imgE = imgE;

  const image1 = () => {
    image.src = power
  }
  const image2 = () => {
    image.src = king
    //imgE = imgE2;
  }
  const image3 = () => {
    image.src = monster 
  }

  // const fs = require('fs');

  // function saveAnnotationsToFile(annotations) {
  //   const filePath = 'annotations.json'; // Path to your existing JSON file

  //   // Read the existing JSON file
  //   fs.readFile(filePath, 'utf8', (err, data) => {
  //     if (err) {
  //       console.error('Error reading file:', err);
  //       return;
  //     }

  //     // Parse the existing JSON data
  //     let existingAnnotations = JSON.parse(data);

  //     // Merge the new annotations with the existing ones
  //     existingAnnotations = [...existingAnnotations, ...annotations];

  //     // Write the updated annotations back to the file
  //     fs.writeFile(filePath, JSON.stringify(existingAnnotations, null, 2), 'utf8', (err) => {
  //       if (err) {
  //         console.error('Error writing file:', err);
  //         return;
  //       }

  //       console.log('Annotations saved to file.');
  //     });
  //   });
  // }

  return (
    <div>
      <div>
        <button
          onClick={toggleTool}>
            { tool === 'rect' ? 'RECTANGLE' : 'POLYGON' }
        </button>
        <button
          onClick={showAnno}>
            Show Annotations
        </button>
        <button onClick={image1}>image1</button>
        <button onClick={image2}>image2</button>
        <button onClick={image3}>image3</button>
        {/* <button onClick={saveAnnotationsToFile}>Save Annotations</button> */}

      </div>

      <p>{strannos}</p>

      <img 
        id="img"
        ref={imgE} 
        src={power} 
        alt="power" />
    </div>
  );
}

export default App;
